import React, { useState } from "react";
import Nav from "react-bootstrap/Nav";
import styles from "./nav.module.scss";

const NavBar = () => {
  const navItems = [
    {
      id: 1,
      title: "База анкет сотрудников",
    },
    {
      id: 2,
      title: "Общая база сотрудников",
    },
    {
      id: 3,
      title: "База сотрудников",
    },
    {
      id: 4,
      title: "Календарь сотрудников",
    },
  ];
  const [activeItem, setActiveItem] = useState(2);

  const hancleClickNavItem = (id: number) => {
    setActiveItem(id);
  };
  return (
    <Nav
      // variant="tabs" defaultActiveKey="/home"
      className={styles.Nav}
    >
      <div className={styles.NavItems}>
        {navItems.map((item) => (
          <Nav.Item
            className={`${styles.NavItem} ${
              activeItem === item.id && styles.NavItem_active
            }`}
            key={item.id}
            onClick={() => hancleClickNavItem(item.id)}
          >
            <Nav.Link
              className={`${styles.NavItemLink} ${
                activeItem === item.id && styles.NavItemLink_active
              }`}
            >
              {item.title}
            </Nav.Link>
          </Nav.Item>
        ))}
      </div>
    </Nav>
  );
};

export default NavBar;
