import React, { useState } from "react";
import { Pagination } from "react-bootstrap";
import NavBar from "./Nav/Nav";
import styles from "./header.module.scss";
import UserBlock from "./UserBlock/UserBlock";

const navItems = [
  {
    id: 1,
    title: "База анкет сотрудников",
  },
  {
    id: 2,
    title: "Общая база сотрудников",
  },
  {
    id: 3,
    title: "База сотрудников",
  },
  {
    id: 4,
    title: "Календарь сотрудников",
  },
];

const Header = () => {
  const [activeItem, setActiveItem] = useState(2);

  const handleClickArrow = (name: string) => {
    setActiveItem((prev) => (name === "prev" ? prev - 1 : prev + 1));
  };

  return (
    <div className={styles.Header}>
      <Pagination className={styles.HeaderArrows}>
        <Pagination.Prev
          onClick={() => handleClickArrow("prev")}
          disabled={activeItem === 1}
        />
        <Pagination.Next
          onClick={() => handleClickArrow("next")}
          disabled={activeItem === 4}
        />
      </Pagination>
      <NavBar activeItem={activeItem} setActiveItem={setActiveItem} />
      <UserBlock />
    </div>
  );
};

export default Header;
