import React, { useEffect, useState } from "react";
import { api } from "../../utils/api";
import Table from "react-bootstrap/Table";
import styles from "./table.module.scss";
import ITable from "./ITable";

const TableComponent = () => {
  const [users, setUsers] = useState<ITable[]>([]);
  const [sortParam, setSortParam] = useState({
    name: "",
    order: true,
  });

  const getUsers = () => {
    api
      .getUsers(sortParam)
      .then((res) => setUsers(res))
      .catch((err) => new Error(err));
  };

  const clickBySort = (param: string) => {
    setSortParam((prev) => {
      let newState = {...prev}
      newState = {
        name: ""
      }
    })
    setSortParam((prev) => {
      let newState = { ...prev };
      newState = {
        ...prev,
        name: param,
        order: !prev.order,
      };
      return newState;
    });
  };

  useEffect(() => {
    getUsers();
  }, [sortParam]);

  const titles = (
    <>
      <tr>
        <th rowSpan={2} className={styles.TableFixColumn}>
          №
        </th>
        <th
          rowSpan={2}
          className={`${styles.TableFixColumn} ${styles.TableFixColumnTwo}`}
          onClick={() => clickBySort("name")}
        >
          <div className={`${styles.TableHeadSort}`}>
            Имя сотрудника
            <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th colSpan={6}>Основная информация</th>
        <th colSpan={2}>Банковская информация</th>
        <th colSpan={11}>Документы сотрудника</th>
        <th colSpan={6}>Информация от HR</th>
      </tr>
      <tr>
        <th>ID номер</th>
        <th>Телефона</th>
        <th onClick={() => clickBySort("gender")}>
          <div className={styles.TableHeadSort}>
            Пол <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Дата рождения</th>
        <th onClick={() => clickBySort("subway")}>
          <div className={styles.TableHeadSort}>
            Метро <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Адрес проживания</th>
        <th onClick={() => clickBySort("bank")}>
          <div className={styles.TableHeadSort}>
            Банк <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Номер карты</th>
        <th onClick={() => clickBySort("nationality")}>
          <div className={styles.TableHeadSort}>
            Гражданство <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Пасспорт</th>
        <th>Кем выдан</th>
        <th>Срок действия</th>
        <th>Место рождения</th>
        <th>Адрес прописки</th>
        <th onClick={() => clickBySort("patent")}>
          <div className={styles.TableHeadSort}>
            Патент <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Срок действия</th>
        <th>СНИЛС</th>
        <th>ИНН</th>
        <th>Мед.книжка</th>
        <th onClick={() => clickBySort("role")}>
          <div className={styles.TableHeadSort}>
            Должность <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th onClick={() => clickBySort("division")}>
          <div className={styles.TableHeadSort}>
            Подразделение
            <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th onClick={() => clickBySort("permission")}>
          <div className={styles.TableHeadSort}>
            Решение <img src="/static/images/table_arrow.svg" alt="arrow" />
          </div>
        </th>
        <th>Источник</th>
        <th>Дата</th>
        <th>Примечание</th>
      </tr>
    </>
  );

  const content =
    users.length > 0 &&
    users.map((user, index) => (
      <tr>
        <td className={`p-3 ${styles.TableFixColumn}`}>{index + 1}</td>
        <td
          className={`p-3 ${styles.TableFixColumn} ${styles.TableFixColumnTwo}`}
        >
          {user?.name || "-"}
        </td>
        <td className="p-3">{user?.id || "-"}</td>
        <td className="p-3">{user?.phone || "-"}</td>
        <td className="p-3">{user?.gender || "-"}</td>
        <td className="p-3">{user?.birthday || "-"}</td>
        <td className="p-3">{user?.subway || "-"}</td>
        <td className="p-3">{user?.address || "-"}</td>

        <td className="p-3">{user?.bank || "-"}</td>
        <td className="p-3">{user?.card || "-"}</td>

        <td className="p-3">{user?.nationality || "-"}</td>
        <td className="p-3">{user?.passport || "-"}</td>
        <td className="p-3">{user?.issued_by || "-"}</td>
        <td className="p-3">{user?.passport_validity || "-"}</td>
        <td className="p-3">{user?.birthplace || "-"}</td>
        <td className="p-3">{user?.residence || "-"}</td>
        <td className="p-3">{user?.patent || "-"}</td>
        <td className="p-3">{user?.residence_validity || "-"}</td>
        <td className="p-3">{user?.snils || "-"}</td>
        <td className="p-3">{user?.identification_code || "-"}</td>
        <td className="p-3">{user?.medical_book || "-"}</td>

        <td className="p-3">{user?.role || "-"}</td>
        <td className="p-3">{user?.division || "-"}</td>
        <td className="p-3">{user?.permission || "-"}</td>
        <td className="p-3">{user?.source || "-"}</td>
        <td className="p-3">{user?.date || "-"}</td>
        <td className="p-3">{user?.comment || "-"}</td>
      </tr>
    ));

  return (
    <div className={styles.Table}>
      <Table responsive className="table-bordered table-striped">
        <thead>{titles}</thead>
        <tbody>{content}</tbody>
      </Table>
    </div>
  );
};

export default TableComponent;
